function determina_codigo_randon(tipo_home_assistant, md5, tipo_device) {

    let _tipo_device = tipo_device;
    let codigo_device_adomo = "";

    if(tipo_device == "00"){

        if (tipo_home_assistant == "switch" || tipo_home_assistant == "light") {

            if (tipo_home_assistant == "light" && brightness != "none" && rgb_color != "none"){
                _tipo_device = "05";
            }else if (tipo_home_assistant == "light" && brightness != "none" && rgb_color == "none") {
                _tipo_device = "24";
            }else{
                _tipo_device = "01";
            }


        } else if (tipo_home_assistant == "sensor") {
            if(device_class == "temperature"){
                _tipo_device = "03";
            }else if(device_class == "motion"){
                 _tipo_device = "13";
            }else if(device_class == "humidity"){
                 _tipo_device = "02";
            }else if(device_class == "illuminance"){
                 _tipo_device = "04";
            }else if (device_class == "data_rate") {
                _tipo_device = "19";
            }else if (device_class == "battery") {
                _tipo_device = "16";
            } else{
                _tipo_device = "31";
            }

        } else if (tipo_home_assistant == "input_boolean") {

            _tipo_device = "33";

        } else if (tipo_home_assistant == "binary_sensor") {

            if (device_class == "door") {
                _tipo_device = "17";
            } else {
                _tipo_device = "32";
            }

        } else if (tipo_home_assistant == "scene") {

            _tipo_device = "34";

        }else if (tipo_home_assistant == "climate") {

            _tipo_device = "10";

        } else if (tipo_home_assistant == "cover") {

            if (device_class == "garage") {
                _tipo_device = "20";
            } else if (device_class == "blind") {
                _tipo_device = "09";
            } else if (device_class == "curtain") {
                _tipo_device = "09";
            } else if (device_class == "gate") {
                _tipo_device = "20";
            } else {
                _tipo_device = "20";
            }


        } else{
            return "00";
        }



    }


    codigo_device_adomo = _tipo_device + md5.substring(0, 22);
    return codigo_device_adomo;
}

